import React from 'react';

import './App.css';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      monsters : [
      {
        name : 'Goblin',
        id : 'mon1'
      },
      {
        name : 'Dragon',
        id : 'mon2'
      },
      {
        name : 'Kapa',
        id : 'mon3'
      },
      {
        name : 'Slime',
        id : 'mon4'
      }
    ]
    }
  }

  render(){
    return(
      <div className = 'App'>
        {this.state.monsters.map(monsters => ( 
          <h1 key = {monsters.id}> {monsters.name} </h1>
        ))}
      </div>
    )
  }
}

export default App;
